mapper.insert:


		SELECT
			NVL(SUBSTR('0000000',LENGTH(TO_NUMBER(PRDT_CODE)+1)+1)||TO_CHAR(TO_NUMBER(PRDT_CODE)+1),'0000001')
		FROM 
		(
			SELECT
				MAX(PRDT_CODE) PRDT_CODE
			FROM SHOP_PRODUCT
		)

	INSERT INTO  SHOP_PRODUCT
	(
		  PRDT_CODE,
		  PRDT_KOR_NAME,
		  PRDT_ENG_NAME,
		  PRDT_SELL_PRICE,
		  PRDT_BRAND_ID,
		  PRDT_DISP_YN,
		  PRDT_DETAIL,
		  PRDT_REG_ID
   )
   VALUES
   (
		#{prdtCode}
		,#{prdtKorName}
		,#{prdtEngName}
		,#{prdtSellPrice}
		,'000'
		,#{prdtDispYn}
		,#{prdtDetail}
		,'kimhk0'
   )
